maxEarn <- function(text.input) {
  split <- unlist(strsplit(text.input,";"))
  period <- as.numeric(split[1])
  range <- 1:period
  values <- as.numeric(unlist(strsplit(split[2]," ")))
  result <- max(sapply(1:(length(values)-period),function(x) {sum(values[range + x])}))
  cat(ifelse(result < 0,yes = 0,no = result))
  cat("\n")
}

